mapper.select:
select pa.id,pr.product_name AS projectName, pr.version AS version,pa.update_description AS updateDescription, app.system_type AS systemType,u.name AS userName, pa.create_time AS createTime,pa.download_url AS downloadUrl, pa.download_total AS downloadTotal,pa.download_complete AS completeNum, pa.force_update AS constraintUpdate,pa.start_time AS startTime, pa.end_time AS endTime,pa.sizes AS 'size',pa.status AS status, pa.dict_ids AS dictId, pa.update_brief AS updateBrief from packages pa LEFT JOIN applications app ON app.id=application_id LEFT JOIN projects pr ON pr.id=app.project_id LEFT JOIN users u ON u.id=pa.user_id where 1=1 and pa.delete_flag=0
and pr.product_name=#{cpd.projectName}
and app.system_type=#{cpd.systemType}
and pa.status=#{cpd.status}
and pa.create_time>=#{cpd.startTime}
and pa.create_time <#{cpd.endTime}
and pa.id=#{cpd.packageId}
order by pa.status

select p.download_url AS downloadUrl, p.download_total AS downloadTotal, p.sizes AS sizes, p.download_complete AS downloadComplete, p.update_description AS updateDescription, p.force_update AS forceUpdate, p.code AS code, pr.version AS version FROM packages p LEFT JOIN applications app on app.id=p.application_id LEFT JOIN projects pr on pr.id=app.project_id where 1 AND p.delete_flag=0
AND pr.product_name=#{param.projectName}
AND app.system_type =#{param.systemType}
ORDER BY p.create_time desc limit 1

select p.id, p.download_url AS downloadUrl, p.download_total AS downloadTotal, p.application_id AS applicationId, p.delete_flag AS deleteFlag, p.status AS status, p.sizes AS sizes, p.dict_ids AS dict_ids, p.download_complete AS downloadComplete, p.update_description AS updateDescription, p.force_update AS forceUpdate, p.user_id AS userId, p.create_time AS createTime, p.start_time AS startTime, p.end_time AS endTime, p.role_id AS roleId from packages p LEFT JOIN applications app on app.id=p.application_id LEFT JOIN projects pr on pr.id=app.project_id where 1 AND p.delete_flag=0
AND pr.product_name=#{param.projectName}
AND app.system_type =#{param.systemType}
order by p.create_time desc limit 1
